import { EntryRecord } from "@holochain-open-dev/utils";
import { Document } from "@holochain-syn/core";
import { Marked, Renderer } from "@ts-stack/markdown";
import hljs from 'highlight.js';
import Automerge from 'automerge';


import { html, TemplateResult } from "lit";
import { AsyncStatus } from "@holochain-open-dev/stores";

export function elemIdToPosition(
  left: boolean,
  elemId: string,
  text: Automerge.Text
): number | undefined {
  for (let i = 0; i < text.length; i+=1) {
    if ((text as any).getElemId(i) === elemId) return left ? i : i + 1;
  }

  return undefined;
}


/**
 * Renders the given AsyncStatus with the given renderers
 */
export function renderAsyncStatus<T>(renderers: {
  complete: (value: T) => TemplateResult;
  error: (error: any) => TemplateResult;
  pending: () => TemplateResult;
}) {
  return (status: AsyncStatus<T>) => {
    switch (status.status) {
      case "complete":
        return renderers.complete(status.value);
      case "error":
        return renderers.error(status.error);
      default:
        return renderers.pending();
    }
  };
}

/**
 * Renders the async status with an <sl-spinner> on pending, and <display-error> on error
 */
export function withSpinnerAndDisplayError<T>(renderers: {
  complete: (value: T) => TemplateResult;
  error: ((error: any) => TemplateResult) | { label: string; tooltip: boolean };
}) {
  return renderAsyncStatus({
    pending: () => html`<div
      style="display: flex; flex-direction: column; align-items: center; justify-content: center; flex: 1;"
    >
      <sl-spinner style="font-size: 2rem;"></sl-spinner>
    </div>`,
    error: (e: any) =>
      typeof renderers.error === "function"
        ? renderers.error(e)
        : html`<display-error
            .headline=${renderers.error?.label}
            .tooltip=${renderers.error?.tooltip}
            .error=${e}
          ></display-error> `,
    complete: renderers.complete,
  });
}


const WE_ICON="data:image/png;base64,"

class HilightRenderer extends Renderer {
  override link(href: string, title : string, text: string) {
    if (href.startsWith("we://")) {
      return `<a href="${href}"${title? ` title="${title}"`:""} target="_blank"><img width="14" style="border-radius:2px; margin-right:2px;" src="${WE_ICON}">${text}</a>`
    }
    return `<a href="${href}"${title? ` title="${title}"`:""} target="_blank">${text}</a>`
  }
}

Marked.setOptions
({
  renderer: new HilightRenderer,
  highlight: (code, lang) =>  {
    if (lang) {
      return hljs.highlight(lang, code).value
    }
    return code
  },
  gfm: true,
  tables: true,
  breaks: false,
  pedantic: false,
  sanitize: true,
  smartLists: true,
  smartypants: false
});

export function sortByDescendantTimestamp(
  notes: EntryRecord<Document>[]
): Array<EntryRecord<Document>> {
  return notes.sort((a, b) => b.action.timestamp - a.action.timestamp);
}

